PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE song (
	id INTEGER NOT NULL, 
	artist VARCHAR(40), 
	song_name VARCHAR(100), 
	source VARCHAR(50), 
	source_type INTEGER, 
	times VARCHAR(20), 
	throughout INTEGER, 
	PRIMARY KEY (id)
);
CREATE TABLE alembic_version (
	version_num VARCHAR(32) NOT NULL, 
	CONSTRAINT alembic_version_pkc PRIMARY KEY (version_num)
);
CREATE TABLE IF NOT EXISTS "answer" (
	id INTEGER NOT NULL, 
	artist VARCHAR(40), 
	song_name VARCHAR(65), 
	source VARCHAR(50), 
	source_type INTEGER, 
	answers VARCHAR(1000), 
	times VARCHAR(20), 
	throughout INTEGER, 
	song_id INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(song_id) REFERENCES song (id)
);
CREATE INDEX ix_song_source_type ON song (source_type);
CREATE INDEX ix_song_times ON song (times);
CREATE INDEX ix_song_artist ON song (artist);
CREATE INDEX ix_song_throughout ON song (throughout);
CREATE INDEX ix_song_song_name ON song (song_name);
CREATE INDEX ix_song_source ON song (source);
CREATE INDEX ix_answer_source_type ON answer (source_type);
CREATE INDEX ix_answer_times ON answer (times);
CREATE INDEX ix_answer_source ON answer (source);
CREATE INDEX ix_answer_artist ON answer (artist);
CREATE INDEX ix_answer_song_name ON answer (song_name);
CREATE INDEX ix_answer_throughout ON answer (throughout);
COMMIT;
